sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
data.options=[];
 var gp = new GlideRecord('u_gamification_criteria');
 gp.addQuery('u_active',true);
	gp.query();
 while(gp.next()){
	 var x={};

	 x.points=gp.getValue('u_criteria_name');
	 x.level=gp.getValue('u_points');	
	  x.threshold=gp.getValue('u_threshold');	
	 data.options.push(x);

 }
})();
