sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
var gr = new GlideRecord('incident');
gr.addQuery('state',6);
gr.addQuery('assignment_group.type','b635c8c8db21ef000e269334ca9619fa');
gr.addQuery('resolved_by',gs.getUserID());
gr.query();
if(gr.next()){
data.resolvedByMe = gr.getRowCount();
}else{
	data.resolvedByMe =0;
}

var gr1 = new GlideRecord('incident');
gr1.addQuery('assignment_group.type','b635c8c8db21ef000e269334ca9619fa');
gr1.query();
if(gr1.next()){
data.totalresolve = gr1.getRowCount();
}else{
data.totalresolve = 0;	
}

	if(data.resolvedByMe != 0 && data.totalresolve != 0){
data.percent = (data.resolvedByMe/data.totalresolve)*100;
	}
	else{
		data.percent = 0;
	}

	data.percentfix = data.percent.toFixed(2);

	var gr2 = new GlideRecord('u_gamification_criteria');
	gr2.addQuery('u_criteria_name','FCR');
	gr2.query();
	if(gr2.next()){
		data.expected = gr2.getDisplayValue('u_expected');
}

	data.percentfix = 76;


})();
