sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var gr = new GlideRecord('u_esmcafe_gamification_table');
	gr.addQuery('u_user',gs.getUserID());
	gr.query();
	if(gr.next()){
		data.points = gr.getDisplayValue('u_points');
		data.level = gr.u_level;
	}else{
		data.points = '0';

	}

	var gr1 = new GlideRecord('u_gamification_level');
	gr1.addQuery('u_level',data.level);
	gr1.query();
	if(gr1.next()){
		data.levname = gr1.getDisplayValue('u_level_name');

	}else{
		data.levname = 'NewBie';
	}
	data.badgeicon = [];
	var gr2 = new GlideRecord('u_gamification');
	gr2.addQuery('u_user',gs.getUserID());
	gr2.addNotNullQuery('u_badge_icon');
	gr2.query();
	while(gr2.next()){
		data.badge = gr2.u_badge_icon;


	var gr3 = new GlideRecord('u_gamification_badge');
	gr3.addQuery('sys_id',data.badge);
	gr3.query();
	while(gr3.next()){
		//data.badgeicon = gr3.getDisplayValue('u_badge_icon');
		data.badgeicon.push(gr3.getDisplayValue('u_badge_icon'));


	}
	}
	if(typeof data.badgeicon[0]=='undefined')
		{

			data.badgeicon[0]=0;
			console.log("First"+data.badgeicon[0]);

		}

	data.badgename = [];
	var gr4 = new GlideRecord('u_gamification');
	gr4.addQuery('u_user',gs.getUserID());
	gr4.addNotNullQuery('u_badge_icon');
	gr4.query();
	while(gr4.next()){
	data.badgename.push(gr4.getDisplayValue('u_badge_icon'));
	}
	console.log(data.badgename+'data.badgename');

})();
