sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */


data.valueone = 0;
var gr = new GlideRecord('u_assessment_metric');
gr.addQuery('Inci_assigned_to',gs.getUserID());
gr.addQuery('asmnt_metric_type.getDisplayValue()','Helpdesk Satisfaction Survey');
gr.addQuery('asmnt_trigger_table','incident');
gr.query();
while(gr.next()){
data.definition = gr.result_metric_definition.getDisplayValue();

if(data.definition == 'Satisfiled'){
data.valueone = data.valueone  + 2;
}
if(data.definition == 'Neutral'){
data.valueone = data.valueone  + 1;
}
if(data.definition == 'Dissatisfied'){
data.valueone = data.valueone  + 0;
}
if(data.definition == 'Very Satisfied'){
data.valueone = data.valueone  + 5;
}
if(data.definition == 'Very Dissatisfied'){
data.valueone  = data.valueone + 0;
}if(data.definition == ''){
data.valueone = data.valueone  + 0;
}

}
console.log(data.valueone);


	var gr2 = new GlideRecord('u_gamification_criteria');
	gr2.addQuery('u_criteria_name','Customer Satisfaction');
	gr2.query();
	if(gr2.next()){
		data.expected = gr2.getDisplayValue('u_expected');
}

data.valueone = 78; //Hardcode

})();
