sp_widget.script:
(function() {

 data.options=[];
 var gp = new GlideRecord('u_esmcafe_gamification_table');
 gp.addQuery('active',true);
 var userId=gs.getUserID();
 gp.orderByDesc('u_points');
 gp.setLimit(4);
 gp.query();

 while(gp.next()){

	 var x={};
	 x.pic='';
	 var sysid=gp.getValue('u_user');
	 var gr1 = new GlideRecord('sys_user');
	 gr1.addQuery();
	 gr1.addQuery('sys_id='+sysid);
	 gr1.query();
	 if(gr1.next())
		 {
			 //image=gr1.getValue('photo');
			 x.user=gr1.getValue('name');
			 x.pic=gr1.getDisplayValue('photo');
		 }
	  if(x.pic=='') //to render alternate photo
				{
					var gr2=GlideRecord('db_image');
						  gr2.addQuery('sys_id=ca6b4715db7de7000e269334ca961912');
							gr2.query();
							if(gr2.next()){
								x.pic=gr2.getDisplayValue('image');
								}
					}

	 //x.user=gp.getDispalyValue('u_user');
	 x.points=gp.getValue('u_points');
	 x.level=gp.getValue('u_level');	
	 data.options.push(x);

 }

})();
