sp_widget.script:
(function(){
	//To getch the logged in user's profile pic
	data.picture='';
	data.role= (gs.hasRole('gamification_admin')||gs.hasRole('itil'));
  var gr01 = new GlideRecord('sys_user');
	gr01.addActiveQuery();
	gr01.addQuery('sys_id',gs.getUserID());
	gr01.query();
	if(gr01.next()){
		data.picture=gr01.getDisplayValue('photo');
	}
	if(data.picture=='')
		{
			var gr2=GlideRecord('db_image');
					gr2.addQuery('sys_id=ca6b4715db7de7000e269334ca961912');
					gr2.query();
					if(gr2.next()){
					data.picture=gr2.getDisplayValue('image');
					}
		}
//To calculate the rank of logged in user	
var grr = new GlideRecord('u_esmcafe_gamification_table');
grr.addQuery('u_user', gs.getUserID());
grr.query();
if(grr.next()){
	data.points=grr.getValue('u_points');
	data.level=grr.getValue('u_level');
		}

 var gp = new GlideRecord('u_esmcafe_gamification_table');
 gp.addQuery('active',true);
 var userId=gs.getUserID();
 gp.orderByDesc('u_points');
 gp.query();
 data.rank=0;
 while(gp.next()){
	 data.rank=data.rank+1;
	 if(gp.u_user == userId){
		 break;
	 }
 }
	var gr = new GlideRecord('u_esmcafe_gamification_table');
gr.addQuery('u_user',gs.getUserID());
gr.query();
if(gr.next()){
data.points = gr.getDisplayValue('u_points');

data.level = gr.getDisplayValue('u_level');
}
	//If logged in user doesnt have any points then the rank is N/A.
	if(typeof data.points=='undefined')
		{
			data.points=0;
			data.rank="N/A";
		}
// To display the progress to next level
var gr4 = new GlideRecord('u_esmcafe_gamification_table');
gr4.addQuery('u_user',gs.getUserID());// Get logged in user
gr4.query();
if(gr4.next()){
data.point = gr4.u_points;// fetch logged in user's current points
data.level1 = gr4.u_level;//fetch logged in user's current level

var gr1 = new GlideRecord('u_gamification_level'); // Query on Level table
gr1.addQuery('u_level',data.level1);
gr1.query();
if(gr1.next()){
data.pointget = gr1.u_point; 
data.rest = data.pointget - data.point;// Find the difference between Level table points and currrent points of user
	data.finalval = 100-data.rest;// To get the completed percentage subtract with 100

}
}else{ // If there are no records in Gamification table for logged in user set the progress to zero.
	data.finalval = 0;
	console.log('else');
}

		data.badgename = [];
	//To display the badge icon in the profile
	var gr3 = new GlideRecord('u_gamification');
	gr3.addQuery('u_user',gs.getUserID());
	gr3.addNotNullQuery('u_badge_icon');
	gr3.query();
	while(gr3.next()){
	data.badgename.push(gr3.getDisplayValue('u_badge_icon'));
	}

})();
