sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.finalval = 0;
var sysid = gs.action.getGlideURI().getMap().get('uid');

var rec = new GlideAggregate('incident');
rec.groupBy('assigned_to');
rec.addAggregate('SUM','calendar_stc');
rec.query();
while(rec.next()){
data.count = rec.getAggregate('SUM','calendar_stc');
data.count1 = data.count/3600;
if(rec.assigned_to == sysid){
data.finalval = parseFloat(data.count1.toFixed(2));
}
}
	var gr = new GlideRecord('u_gamification_criteria');
	gr.addQuery('u_criteria_name','Resolution Time');
	gr.query();
	if(gr.next()){
		data.expected = parseFloat(gr.getDisplayValue('u_expected'));
}

	data.finalval = 1.5;

})();
