sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
var count = new GlideAggregate('incident');
count.addEncodedQuery('state=6^resolved_atONToday@javascript:gs.beginningOfToday()@javascript:gs.endOfToday()^assignment_group.typeLIKEb635c8c8db21ef000e269334ca9619fa^resolved_byDYNAMIC90d1921e5f510100a9ad2572f2b477fe');
count.query();
if(count.next()){
data.total = count.getRowCount();

}else{
data.total = 0;	
}
	var gr = new GlideRecord('u_gamification_criteria');
	gr.addQuery('u_criteria_name','Number of calls in a day');
	gr.query();
	if(gr.next()){
		data.expected = gr.getDisplayValue('u_expected');
}

})();
