sp_widget.script:
(function(){

	var sysid = gs.action.getGlideURI().getMap().get('uid'); //window.location.href
  data.sysid=sysid;
	data.picture='';
  var gr01 = new GlideRecord('sys_user');
	gr01.addActiveQuery();
	gr01.addQuery('sys_id',sysid);
	gr01.query();
	if(gr01.next()){
		data.picture=gr01.getDisplayValue('photo');
		data.name=gr01.getDisplayValue('name');
	}
	if(data.picture=='')
		{
			var gr2=GlideRecord('db_image');
					gr2.addQuery('sys_id=ca6b4715db7de7000e269334ca961912');
					gr2.query();
					if(gr2.next()){
					data.picture=gr2.getDisplayValue('image');
					}
		}

 var gp = new GlideRecord('u_esmcafe_gamification_table');
 gp.addQuery('active',true);
 var userId=sysid;
 gp.orderByDesc('u_points');
 gp.query();
 data.rank=0;
 while(gp.next()){
	 data.rank=data.rank+1;
	 if(gp.u_user == userId){
		 break;
	 }
 }

var gr = new GlideRecord('u_esmcafe_gamification_table');
gr.addQuery('u_user',sysid);
gr.query();
if(gr.next()){

   data.points = gr.getDisplayValue('u_points');
  // data.name=gr.getDisplayValue('u_user');
   data.level = gr.getDisplayValue('u_level');
}

	if(typeof data.points=='undefined')
		{
			data.points=0;
			data.rank="N/A";
		}

var gr1 = new GlideRecord('u_gamification_level');

switch(data.level.toString()) {

case 'Level 1':

gr1.addQuery('u_level','level 2');
gr1.query();
if(gr1.next()){

data.attain = gr1.getDisplayValue('u_point');
data.takediff = data.attain-data.points;

}
		break;
case 'Level 2':

gr1.addQuery('u_level','level 3');
gr1.query();
if(gr1.next()){

data.attain = gr1.getDisplayValue('u_point');
data.takediff = data.attain-data.points;

}
		break;
case 'Level 3':

gr1.addQuery('u_level','level 4');
gr1.query();
if(gr1.next()){

data.attain = gr1.getDisplayValue('u_point');
data.takediff = data.attain-data.points;

}else{
	data.attain = 300;

data.takediff =data.attain-data.points;

}
break;
default:
data.attain = '300';

data.takediff = data.attain-data.points;

}

	data.finaldiff = 100-data.takediff;
		if(data.finaldiff>=0){
			data.finalval = data.finaldiff;

	}else{
		data.finalval = data.points;

	}
		data.badgename = [];
	var gr3 = new GlideRecord('u_gamification');
	gr3.addQuery('u_user',sysid);
	gr3.addNotNullQuery('u_badge_icon');
	gr3.query();
	while(gr3.next()){
	data.badgename.push(gr3.getDisplayValue('u_badge_icon'));
	}

})();
