sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.login_page = $sp.getValue('login_page');
	var rank=0;
	var gr=GlideRecord('u_esmcafe_gamification_table');
	gr.addQuery('active' , true);
        data.options = [];
        gr.orderByDesc('u_points');
        gr.query();
        while(gr.next()) {
					rank++;
					var name='';
					var image='';
					var display;
					var sysid=gr.getValue('u_user');


					var gr1=GlideRecord('sys_user');
					gr1.addQuery('sys_id='+sysid);
					gr1.query();
					if(gr1.next())
						{
							image=gr1.getValue('photo');
							name=gr1.getValue('name');
							display=gr1.getDisplayValue('photo');
						}

						if(gr1.getDisplayValue('photo')=='') //to render alternate photo
							{
									var gr2=GlideRecord('db_image');
									gr2.addQuery('sys_id=ca6b4715db7de7000e269334ca961912');
									gr2.query();
									if(gr2.next()){
										display=gr2.getDisplayValue('image');
									}
							}


            data.options.push({ 'id': sysid , 'points': gr.getValue('u_points'), 'levels': gr.getValue('u_level'),'name':name ,'image':display, 'rank':rank});
        }



})();
