sp_widget.script:
(function() {

 data.options=[];
 var gp = new GlideRecord('u_esmcafe_gamification_table');
 gp.addQuery('active',true);
 var userId=gs.getUserID();
 gp.orderByDesc('u_points');
 gp.setLimit(5);
 gp.query();

 while(gp.next()){
	 var x={};
	 var gr1 = new GlideRecord('sys_user');
	 gr1.addActiveQuery();
	 gr1.addQuery('user_name',gp.getDisplayValue('u_user'));
	 gr1.query();
	 if(gr1.next())
		 {
			 x.user=gr1.getValue('name');
		 }

	 x.points=gp.getValue('u_points');
	 x.level=gp.getValue('u_level');	
	 data.options.push(x);

 }

})();
