sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	/****************************/
	//To calculate the resolve time and insert it into the gamification table.
	/****************************/

	//Storing the sysid of Resolution time from Gamification criteria table.
	var badgeName = '0df8dd3bdb05230008d749ee3b9619f2';
	//Converting the Resolve time value of Incident into Hours.
	var ResolveTimeInHours = current.calendar_stc/3600;
	var gr1 = new GlideRecord('u_gamification_criteria');
	gr1.addQuery('sys_id',badgeName);
	gr1.query();
	if(gr1.next()){

		//Calculate the point based on Resolve time and insert it into the Gamification table.
		if(ResolveTimeInHours <= gr1.u_threshold){
			var gr = new GlideRecord('u_gamification');
			gr.addQuery('u_badge_names','0df8dd3bdb05230008d749ee3b9619f2');
			gr.addQuery('u_user',current.assigned_to);
			gr.query();
			if(gr.next()){
				gs.log('insdeif');
				var totalpoints = parseInt(gr.u_total_points);
				gr.u_total_points = totalpoints + parseInt(gr1.u_points);
				gs.log(gr.u_total_points+'gr.u_total_points');
				gr.update();
			}else{
				gs.log('insideelsepart');
				gr.initialize();
				gr.u_user = current.assigned_to;
				gr.u_badge_names = badgeName;
				//Fetch the point value from Gamification criteria table.
				gr.u_total_points = gr1.u_points;
			//	gr.u_incident_number = current.number;
				gr.insert();
			}
		}
	}

})(current, previous);
