sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	 // To add/update the record in Leaderboard table when the knowledge article is published.
	var author = current.author;
	 // Criteria is Knowledge Article/ Knowledge Feedback/Co
	 var badgeName = '12d8117bdb05230008d749ee3b961931';
	//Querying the Gamification criteria table to fetch the threshold values
	 var grr = new GlideRecord('u_gamification_criteria');
	 grr.addQuery('sys_id',badgeName);
	 grr.query();

	 var gr = new GlideRecord('u_gamification');
	 gr.addEncodedQuery('u_badge_names=12d8117bdb05230008d749ee3b961931^u_user='+author);
	 gr.query();
	// Check whether the record is already present in Leaderboard table, if present update the record 
	 if(grr.next()){
		 var x=parseFloat(grr.u_points);
		 if(gr.getRowCount()!=0){
			if(gr.next()){
				var y=parseFloat(gr.u_total_points)+x;
				gr.u_total_points=y;
				gr.update();	}
		}
		else{// If there is no record present in leaderboard table for the Service Desk agent, insert the record when the article is published.
			var gr1 = new GlideRecord('u_gamification');
			gr1.initialize();
			gr1.u_user = author;		
			gr1.u_total_points=x;
			gr1.u_badge_names=badgeName;
		//	gs.log("******02 "+gr.u_total_points);
			gr1.insert();

		}
	 }

})(current, previous);
