sysauto_script.script:
/*************************/
//This scheduled job inserts record into leaderboard table based ont he points the user earned in gamificationtable.
/*************************/

//Deletes the record in leaderboard table initially to avoid duplication of records.
var gr1 = new GlideRecord('u_esmcafe_gamification_table');
gr1.query();
while(gr1.next()){
	gr1.deleteMultiple();
}

//Queries the gamification table to fetch the records by grouping the user and adding the points gained by the user.

var rec = new GlideAggregate('u_gamification');
rec.groupBy('u_user');
rec.addAggregate('SUM','u_total_points');
rec.query();

while(rec.next()){
	var count = rec.getAggregate('SUM','u_total_points');
	//Inserts the record into leadership table.
	var gr1 = new GlideRecord('u_esmcafe_gamification_table');
	gr1.initialize();

	var x='';
	var y=parseInt(count);
	// Populate the level value in Leaderboard table as per the gamification level table's threshold value.
	var grr = new GlideRecord('u_gamification_level'); 
    grr.orderByDesc('u_level');
	grr.addActiveQuery();
 	grr.query();

	while(grr.next()){
	var point= parseInt(grr.u_point);
	if (y<=point) {
		x=grr.getValue('u_level');
 	}	
	}	
	gr1.u_level=x;
	gr1.u_user = rec.u_user;
	gr1.u_points = count;
	gr1.insert();

}
